"use strict";

function replaceChar(char) {
    return sanitizeString.replacements[char] || sanitizeString.defaultReplacement;
}

function sanitizeString(str) {
    return str.replace(sanitizeString.unwantedChars, replaceChar);
}

sanitizeString.unwantedChars = /[^a-z0-9_\-]/gi;

sanitizeString.replacements = {
    " ": "-",
    "ä": "ae",
    "ü": "ue",
    "ö": "oe",
    "Ä": "Ae",
    "Ö": "Oe",
    "Ü": "Ue",
    "ß": "ss"
};

sanitizeString.defaultReplacement = "";

module.exports = sanitizeString;